CREATE TABLE t_user(ID INT AUTO_INCREMENT PRIMARY KEY, username VARCHAR(255) not null, password VARCHAR(255) not null, email VARCHAR(255) not null, head_img VARCHAR(255) not null);
CREATE TABLE T_FILES (ID INTEGER DEFAULT NOT NULL AUTO_INCREMENT, USER_ID INTEGER NOT NULL, FILE_NAME VARCHAR(255) NOT NULL, SUBMIT_FILE_NAME VARCHAR(255) NOT NULL, is_dir int(1) NOT NULL, path_name VARCHAR(255) NOT NULL);
